-- exec Admin_GetSharedWithCentralUserCreate       PROCEDURE [dbo].[Admin_GetSharedWithCentralUser]             AS           BEGIN             BEGIN TRY                BEGIN	-- Below id for Shared and Central RoleType Id		SELECT DISTINCT RM.RoleId,		RM.RoleName		From Admin_UserRoleMapping URM WITH(NOLOCK)		LEFT JOIN Admin_RoleMaster RM WITH(NOLOCK) ON RM.RoleId = URM.RoleId		where URM.RoleTypeId IN ( 'D1E03B48-C313-4BB6-928C-E62C44E1DBDE','2BA70571-63B3-4193-9F31-BDBCC59ED08B')		ORDER BY RM.RoleName		END     END TRY                             BEGIN CATCH                DECLARE @StrProcedure_Name varchar(500), @ErrorDetail varchar(1000), @ParameterList varchar(2000)                          SET @StrProcedure_Name=ERROR_PROCEDURE()                                          SET @ErrorDetail=ERROR_MESSAGE()                                          EXEC Admin_InsertErrorDetail @StrProcedure_Name=@StrProcedure_Name,@ErrorDetail=@ErrorDetail,@ParameterList=@ParameterList                                          END CATCH                     END